class Semaforo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Semaforo : 'desligado',
        }
    }

    verde = () => {
        document.getElementById('liga').style.color = type;
        this.setState({
            Semaforo: 'verde',
        });
        this.style.setState({
            color:'green',     
        });

    }
    amarelo = () => {
        this.setState({
            Semaforo: 'amarelo',
        });
    }
    vermelho = () => {
        this.setState({
            Semaforo: 'vermelho',
        });
    }
    render() {
        return(
            <div>
                <h2 id={'liga'}>{this.state.Semaforo}</h2>
                <button onClick={this.verde}>verde</button>
                <button onClick={this.amarelo}>amarelo</button>
                <button onClick={this.vermelho}>vermelho</button>
            </div>
        )
    }
}
export default Semaforo;
